mapper.select:

SELECT id, music_name AS name
FROM `music_resources`
WHERE music_name LIKE #{blurry}
LIMIT #{resultCount}


SELECT id, music_list_name AS name
FROM `music_list`
WHERE music_list_name LIKE #{blurry}
LIMIT #{resultCount}


SELECT id, music_name AS name
FROM `music_resources`
WHERE music_author LIKE #{blurry}
LIMIT #{resultCount}
