mapper.select:
SELECT count(*)
FROM `user`
WHERE user_email=#{email} AND user_password=#{password};

mapper.update:
UPDATE `user`
SET gmt_modified=NOW(), user_password=#{newPassword}
WHERE user_uid=#{uid};

mapper.select:
SELECT user_last_login_time
FROM user
WHERE user_uid=#{uid};

mapper.update:
UPDATE `user`
SET user_last_login_time=NOW(), gmt_modified=NOW()
WHERE user_uid=#{uid};
